def get_list1(html,titil):
    #字典
    list ={}

    list['标题'] =titil

    #图片
    name1 = html.find_element_by_class_name('first_img')
    list['图片']=name1.get_attribute('src')

    #别名
    name2 = html.find_element_by_class_name('sub_title')
    list['别名'] = name2.text

    t1 = html.find_elements_by_class_name('title')
    t2 = html.find_elements_by_class_name('alert')
    t3 = html.find_elements_by_class_name('content')

    for i in range(len(t1)):
        list['分栏'+str(i)]=t1[i].text
        list['判断'+str(i)] = t2[i].text
        list['正文'+str(i)] = t3[i*2+1].text

    # print(list)
    return list

#能不能吃的提取代码
def get_list(html):
    list ={}

    #名字
    name1 = html.find_element_by_class_name('cie-detail-title')
    list['name1']=name1.text

    #别名
    name2 = html.find_element_by_class_name('cie-detail-desc')
    list['name2']=name2.text

    #获取图片地址
    _url = html.find_element_by_class_name('cie-detail-focus')
    list['url']=_url.get_attribute('src')

    #正文
    _m1 = html.find_elements_by_class_name('cie-detail-item')
    gde= []
    for ad in _m1:
        gde.append(ad.text)
    list['m1']=gde

    #tip
    _m2 = html.find_element_by_class_name('cie-detail-tipguide')
    list['小贴士'] = _m2.text

    print(list)
    return list

